    private void btnLoginMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnLoginMouseClicked
        
        // Get the username and password from the input fields
        String username = txtUsername.getText().trim();
        String password = txtPassword.getText().trim();

        // Check if username or password is empty
        if (username.isEmpty() || password.isEmpty()) {
            // Show error message if any field is empty
            JOptionPane.showMessageDialog(this, "Username and password cannot be empty", "Login Error", JOptionPane.ERROR_MESSAGE);
            return;  // Exit the method if fields are empty
        }

        // Database connection details
        String url = "jdbc:mysql://localhost:3306/summercamp";
        String user = "root";
        String dbPassword = "Java@2024";

        Connection conn = null;
        PreparedStatement pstmt = null;
        ResultSet rs = null;

        try {
            // Load the MySQL JDBC driver
            Class.forName("com.mysql.cj.jdbc.Driver");

            // Establish connection to the database
            conn = DriverManager.getConnection(url, user, dbPassword);

            // Prepare SQL query to check user credentials
            String sql = "SELECT * FROM user WHERE username = ? AND password = ?";
            pstmt = conn.prepareStatement(sql);
            pstmt.setString(1, username);
            pstmt.setString(2, password);
            rs = pstmt.executeQuery();

            // Check if the user exists
            if (rs.next()) {
                // Get the user's role and navigate accordingly
                String role = rs.getString("role");
                if ("Admin".equalsIgnoreCase(role)) {
                    // If the user is an Admin, open the Admin form
                    adminForm admin = new adminForm();
                    admin.setVisible(true);
                } else {
                    // If the user is not an Admin, navigate to other roles
                    // For example, the Cashier form can be opened for a cashier role
                    // CashierForm cashier = new CashierForm();
                    // cashier.setVisible(true);
                }
                // Close the login form after successful login
                this.dispose();
            } else {
                // If no matching user is found, show an error message
                JOptionPane.showMessageDialog(this, "Invalid username or password", "Login Error", JOptionPane.ERROR_MESSAGE);
            }

        } catch (ClassNotFoundException e) {
            // Handle error if the MySQL JDBC Driver is not found
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "MySQL JDBC Driver not found: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        } catch (SQLException e) {
            // Handle SQL-related errors
            e.printStackTrace();
            JOptionPane.showMessageDialog(this, "Error during login: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
        } finally {
            // Optionally, clean up and close connections (not shown here for simplicity)
        }
        
    }//GEN-LAST:event_btnLoginMouseClicked

    // This method handles the mouse click event on the "Register" button.
    // When the button is clicked, a new registration form (formRegister) is created and made visible.
    // The current window is then closed by calling dispose().
    private void btnRegisterMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnRegisterMouseClicked
        formRegister register = new formRegister();  // Create a new instance of the registration form
        register.setVisible(true);  // Show the registration form
        dispose();  // Close the current window
    }//GEN-LAST:event_btnRegisterMouseClicked

