    // This method handles the action event triggered when the "Show Password" checkbox is selected or deselected.
    // If the checkbox is selected, the password field's echo character is set to 0 (making the password visible).
    // If the checkbox is deselected, the password field's echo character is set to '*' (hiding the password).
    private void chkPasswordActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_chkPasswordActionPerformed
        if(chkPassword.isSelected()){
            txtPassword.setEchoChar((char)0);  // Show password
        } else {
            txtPassword.setEchoChar('*');      // Hide password
        }
    }//GEN-LAST:event_chkPasswordActionPerformed

